-- Version = 1.0.0, Package = CK.Resource---- Edits a BigText Data ressource's Val by a @NewVal-- if ressource doesn't exists, create it-- if BigText ressource doesn't exists, create it--create procedure CK.sResBigTextDataEdit(	@ActorId	int,	@ResId		int,	-- Pass 0 or null to create the resource.	@NewVal		nvarchar(400))asbegin	declare @resName varchar(96);	select @resName = ResName		from CK.tRes		where ResId = @ResId;			if @@RowCount = 0	begin			exec CK.sResCreate @ActorId, @ResName, @ResId output;		end	declare @exist bit;	set @exist = 0;		select @exist = 1		from CK.tResBigTextData		where ResId = @ResId;		if @exist = 0		begin					exec CK.sResBigTextDataCreate @ResName, @NewVal;				end	else		begin					update CK.tResBigTextData set Val = @NewVal where ResId = @idRes;				end	return 0;end