-- Version = 1.0.0, Package = CK.Resource---- Deletes a BigText Data ressource-- Deletes ressource too if @AllRes bit equals 1--create procedure CK.sResBigTextDataRemove(	@ActorId	int,	@ResId		int,	@AllRes		bit)asbegin	delete from CK.tResBigTextData where ResId = @ResId;	if @AllRes = 1 exec CK.sResRemove @ActorId, @ResId;	return 0;end