-- Version = 1.0.0, Package = CK.Resource---- Creates a String Data ressource-- if @ResName not exists, create it--create procedure CK.sResStringDataCreate	@ResName	varchar(96),	@Val		varchar(400)asbegin		declare @idRes int;	declare @exist bit;	set @exist = 0;		select @exist = 1, @idRes = ResId		from CK.tRes		where ResName = @ResName;			if @exist = 0 exec CK.sResCreate @ResName, @idRes		if @idRes is null return 0;		insert into CK.tResStringData ( ResId, Val ) values ( @idRes, @Val );	return 0;	end