-- Version = 1.0.0, Package = CK.Resource---- Edits a String Data ressource's Val by a @NewVal-- if ressource doesn't exists, create it-- if string ressource doesn't exists, create it--create procedure CK.sResStringDataEdit(	@ActorId	int,	@ResName	varchar(96),	@NewVal		varchar(400))asbegin	declare @idRes int;	select @idRes = ResId		from CK.tRes		where ResName = @ResName;			if @@RowCount = 0	begin			exec CK.sResCreate @ResName, @idRes;		end	declare @exist bit;	set @exist = 0;		select @exist = 1		from CK.tResStringData		where ResId = @idRes;		if @exist = 0		begin					exec CK.sResStringDataCreate @ResName, @NewVal;				end	else		begin					update CK.tResStringData set Val = @NewVal where ResId = @idRes;				end	return 0;end